import React from 'react'
import '../resources/css/Card.css'
import { useSelector} from 'react-redux'
import { actions } from '../store/Index'
import { useNavigate } from 'react-router-dom'

function ItemComponent({props}) {

  let cartItems = useSelector((state) => state.cartItems)
  let dispatch=useDispatch()
  let navigate = useNavigate()

  const AddToCart = (props) => (dispatch, getState) => {
    const state=getState()
    const {cartItems} = state

    if (cartItems[props.id]) {
      dispatch(actions.AddToCart({productId: props.id}))
    } else {
      dispatch(actions.AddToCart({ productId: props.id, productName: props.name }));
    }

    navigate('/cart', {
      productId: props.id,
      productName: props.name,
      counter: cartItems[props.id],
    });
  }
  return (
    <div className='card card-container'>
      <h4 className="card-title card-title-custom">{props.name}</h4>
      <img src={props.picture} className='card-img-top cards' alt='card-img-cap'></img>
      <hr className='hr-top'/>
      <div className="card-body">
        <p className="card-text">Price: <span>{props.price}$</span></p>
        <p className="card-text">In store: <span>{props.avalible}</span></p>
        <p className="card-text characters"> <p className='about-span'>About:</p>{props.characteristics}</p>
        <button className='btn btn-success col-8' onClick={AddToCart}>Add To Cart</button>
      </div>
      <h1>Counter: {cartItems[props.id] || 0}</h1>
    </div>
  )
}

export default ItemComponent
