import React from 'react'
import '../resources/css/Card.css'
import { useSelector, useDispatch} from 'react-redux'
import { actions } from '../store/Index'

function ItemComponent({props}) {

  let cart = useSelector((state) => state.cartItems)
  let dispatch=useDispatch()

  const AddToCart = () => {
    dispatch(actions.AddToCart( {productId: props.id}))
  }
  return (
    <div className='card card-container'>
      <h4 class="card-title card-title-custom">{props.name}</h4>
      <img src={props.picture} className='card-img-top cards' alt='card-img-cap'></img>
      <hr className='hr-top'/>
      <div class="card-body">
        <p class="card-text">Price: <span>{props.price}$</span></p>
        <p class="card-text">In store: <span>{props.avalible}</span></p>
        <p class="card-text characters"> <p className='about-span'>About:</p>{props.characteristics}</p>
        <button className='btn btn-success col-8' onClick={AddToCart}>Add To Cart</button>
      </div>
      <h1>Counter: {cartItems[props.id] || 0}</h1>
    </div>
  )
}

export default ItemComponent
