import React from 'react'
import '../resources/css/Card.css'
import { useState } from 'react'

function ItemComponent({props}) {
  let AmountChange = () => {
    let [number, setNumber] = useState(0)

    let handleEventChange = (e) => {
      setNumber(e.target.value)
    }
  }
  return (
    <div className='card card-container'>
      <img src={props.picture} className='card-img-top cards' alt='card-img-cap'></img>
      <div class="card-body">
        <h4 class="card-title card-title-custom">{props.name}</h4>
        <p class="card-text">Price: <span>{props.price}$</span></p>
        <p class="card-text">In store: <span>{props.avalible}</span></p>
        <p class="card-text characters">About: <br/>{props.characteristics}</p>
        <hr/>
        <form className='order-info row'>
          <label htmlFor="numberInput" className='col-5'>Amount:</label>
          <input
            className='col-2 ml-0'
            type="number"
            id="numberInput"
            name="numberInput"
            value={number}
            onChange={handleEventChange}
          />
          <button className='btn btn-success col-4 offset-1' type='submit' value='potvrdi'>Buy</button>
        </form>
      </div>
    </div>
  )
}

export default ItemComponent
